package Assignment_Loop_lms;

public class Q1SumNaturalNumber {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int num =10, sum =0;
		for( int i =1; i <=num ; i++)
		{
			sum = sum + i;
			//sum += i;
		}
		System.out.println("Sum of n natural numbers:"+sum);
	}

}
