package Assignment_Loop_lms;

public class Q2MultiplicationTabel {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Multiplication table of 24");
		for(int i=1;i<=10;i++)
		{
			
			System.out.println("24 * "+i+" = "+i*24);
		}
		System.out.println("\n");
		System.out.println("Multiplication table of 50");
		for(int i=1;i<=10;i++)
		{
			System.out.println("50 * "+i+" = "+i*50);
		}
		System.out.println("\n");
		System.out.println("Multiplication table of 29");
		for(int i=1;i<=10;i++)
		{
			System.out.println("29 * "+i+" = "+i*29);
		}
	}

}
