package SimpleCode;

public class Table5forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=10;i++)
		{
			System.out.println("5 *"+i+" = "+i*5);
		}
		System.out.println("\n");
		for(int i=1;i<=10;i++)
		{
			System.out.println("8 *"+i+" = "+i*8);
		}
	}

}
