package SimpleCode;

public class example {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Odd number
		for(int i=1;i<=100;i=i+2)
		{
			System.out.println("odd number = "+i);
		}
		//even number
		for(int i=0;i<=100;i=i+2)
		{
			System.out.println("even number = "+i);
		}
	}

}
