package example;

public class Arrayexample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[] = {10,20,30,25,1};
		for(int i=0;i<5;i++)
		{
			System.out.println(a[i]);
		}
		char ar[]= {'a','b','c'};
		for (int i=0;i<3;i++)
		{
			System.out.println(ar[i]);
		}
		int ab[] = new int[5]; //declaration of array
		ab[0] =10;
		ab[1] =32;
		ab[2]= 2;
		ab[3]= 30;
		ab[4]=4; //assign values to array
		
	}

}
