package example;

public class Concatexample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str ="welcome";
		String name ="sai";
		
		//concatenation
		System.out.println(str.concat(name));
		System.out.println(str.concat("a"));
		
		//contains
		System.out.println(str.contains("wel"));
		System.out.println(str.contains(name));
		
		System.out.println();
	}

}
